
public class IfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 2;
		
		if(n==0)
		{
			System.out.println("Nothing");
		}else if(n%2==0)
		{
			System.out.println("Even");
		}
		else
		{
			System.out.println("Odd");
		}
	}

}
